import React, { useState } from 'react';
import { v4 as uuidv4 } from 'uuid';
import './ConnectionPanel.css';

const ConnectionPanel = ({ onConnect }) => {
  const [roomId, setRoomId] = useState('');
  const [isConnecting, setIsConnecting] = useState(false);

  const handleCreateRoom = async () => {
    const newRoomId = uuidv4().slice(0, 8);
    setIsConnecting(true);
    try {
      await onConnect(newRoomId, true);
    } catch (error) {
      console.error('Failed to create room:', error);
    } finally {
      setIsConnecting(false);
    }
  };

  const handleJoinRoom = async () => {
    if (!roomId.trim()) {
      alert('Please enter a room ID');
      return;
    }

    setIsConnecting(true);
    try {
      await onConnect(roomId.trim(), false);
    } catch (error) {
      console.error('Failed to join room:', error);
    } finally {
      setIsConnecting(false);
    }
  };

  return (
    <div className="connection-panel">
      <div className="connection-card">
        <h2>Connect to Whiteboard</h2>
        <p>Choose how you want to connect:</p>

        <div className="connection-options">
          <div className="option">
            <h3>Create New Session</h3>
            <p>Start a new whiteboard session and share the room ID with others</p>
            <button 
              onClick={handleCreateRoom}
              disabled={isConnecting}
              className="btn btn-primary"
            >
              {isConnecting ? 'Creating...' : 'Create Room'}
            </button>
          </div>

          <div className="divider">OR</div>

          <div className="option">
            <h3>Join Existing Session</h3>
            <p>Enter the room ID shared by the other person</p>
            <div className="input-group">
              <input
                type="text"
                placeholder="Enter Room ID"
                value={roomId}
                onChange={(e) => setRoomId(e.target.value.toUpperCase())}
                disabled={isConnecting}
                className="room-input"
                maxLength={8}
              />
              <button 
                onClick={handleJoinRoom}
                disabled={isConnecting || !roomId.trim()}
                className="btn btn-secondary"
              >
                {isConnecting ? 'Joining...' : 'Join Room'}
              </button>
            </div>
          </div>
        </div>

        <div className="info-section">
          <h4>How it works:</h4>
          <ul>
            <li>Direct peer-to-peer connection (no server latency)</li>
            <li>Real-time collaborative drawing</li>
            <li>Multi-page canvas support</li>
            <li>No data stored on servers</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default ConnectionPanel;
