import React, { useCallback, useRef, useState } from 'react';
import './App.css';
import ConnectionPanel from './components/ConnectionPanel';
import PageManager from './components/PageManager';
import Toolbar from './components/Toolbar';
import Whiteboard from './components/Whiteboard';
import WebRTCManager from './services/WebRTCManager';

function App() {
  const [isConnected, setIsConnected] = useState(false);
  const [connectionId, setConnectionId] = useState('');
  const [isHost, setIsHost] = useState(false);
  const [isConnecting, setIsConnecting] = useState(false);
  const [currentTool, setCurrentTool] = useState('pen');
  const [currentColor, setCurrentColor] = useState('#000000');
  const [strokeWidth, setStrokeWidth] = useState(2);
  const [currentPage, setCurrentPage] = useState(0);
  const [totalPages, setTotalPages] = useState(1);
  const [drawingData, setDrawingData] = useState([[]]);

  const webrtcManagerRef = useRef(null);

  // Utility function for copying text with mobile fallback
  const copyToClipboard = (text) => {
    if (navigator.clipboard && navigator.clipboard.writeText) {
      // Modern browsers with HTTPS
      navigator.clipboard.writeText(text).then(() => {
        console.log('‚úÖ Copied to clipboard');
      }).catch(err => {
        console.error('‚ùå Copy failed:', err);
      });
    } else {
      // Fallback for mobile/HTTP
      const textArea = document.createElement('textarea');
      textArea.value = text;
      textArea.style.position = 'fixed';
      textArea.style.opacity = '0';
      document.body.appendChild(textArea);
      textArea.focus();
      textArea.select();
      const success = document.execCommand('copy');
      document.body.removeChild(textArea);

      if (success) {
        console.log('‚úÖ Copied to clipboard (fallback method)');
      } else {
        console.error('‚ùå Copy failed (fallback method)');
      }
    }
  };

  const handleConnection = useCallback(async (roomId, isHostRole) => {
    try {
      setConnectionId(roomId);
      setIsHost(isHostRole);
      setIsConnecting(true);

      if (!webrtcManagerRef.current) {
        webrtcManagerRef.current = new WebRTCManager();
      }

      webrtcManagerRef.current.onDataReceived = (data) => {
        handleRemoteDrawing(data);
      };

      webrtcManagerRef.current.onConnectionStateChange = (connected) => {
        setIsConnected(connected);
        if (!connected) {
          setIsConnecting(false);
        }
      };

      await webrtcManagerRef.current.initialize(roomId, isHostRole);

      if (isHostRole) {
        // For host, show the room ID immediately so they can share it
        console.log('Room created! Share this Room ID:', roomId);
      }
    } catch (error) {
      console.error('Connection failed:', error);
      alert('Failed to establish connection: ' + error.message);
      setIsConnecting(false);
      setConnectionId('');
    }
  }, []);

  const handleRemoteDrawing = (data) => {
    if (data.type === 'drawing') {
      setDrawingData(prev => {
        const newData = [...prev];
        while (newData.length <= data.page) {
          newData.push([]);
        }
        newData[data.page] = [...newData[data.page], data.stroke];
        return newData;
      });
    } else if (data.type === 'clear') {
      setDrawingData(prev => {
        const newData = [...prev];
        if (newData[data.page]) {
          newData[data.page] = [];
        }
        return newData;
      });
    } else if (data.type === 'pageChange') {
      setTotalPages(Math.max(totalPages, data.totalPages));
    }
  };

  const handleLocalDrawing = (stroke) => {
    if (webrtcManagerRef.current && isConnected) {
      webrtcManagerRef.current.sendData({
        type: 'drawing',
        stroke: stroke,
        page: currentPage
      });
    }

    setDrawingData(prev => {
      const newData = [...prev];
      while (newData.length <= currentPage) {
        newData.push([]);
      }
      newData[currentPage] = [...newData[currentPage], stroke];
      return newData;
    });
  };

  const handleClearPage = () => {
    if (webrtcManagerRef.current && isConnected) {
      webrtcManagerRef.current.sendData({
        type: 'clear',
        page: currentPage
      });
    }

    setDrawingData(prev => {
      const newData = [...prev];
      if (newData[currentPage]) {
        newData[currentPage] = [];
      }
      return newData;
    });
  };

  const handlePageChange = (pageIndex) => {
    setCurrentPage(pageIndex);

    if (webrtcManagerRef.current && isConnected) {
      webrtcManagerRef.current.sendData({
        type: 'pageChange',
        page: pageIndex,
        totalPages: Math.max(totalPages, pageIndex + 1)
      });
    }
  };

  const addNewPage = () => {
    const newPageIndex = totalPages;
    setTotalPages(prev => prev + 1);
    setDrawingData(prev => [...prev, []]);
    setCurrentPage(newPageIndex);

    if (webrtcManagerRef.current && isConnected) {
      webrtcManagerRef.current.sendData({
        type: 'pageChange',
        page: newPageIndex,
        totalPages: totalPages + 1
      });
    }
  };

  const handleDisconnect = () => {
    if (webrtcManagerRef.current) {
      webrtcManagerRef.current.disconnect();
    }
    setIsConnected(false);
    setIsConnecting(false);
    setConnectionId('');
    setIsHost(false);
  };

  // Show connection panel if not connected and no room ID
  const showConnectionPanel = !connectionId;

  return (
    <div className="app">
      <header className="app-header">
        <h1>WebRTC Whiteboard</h1>
        <div className="connection-status">
          {isConnected ? (
            <span className="connected">‚úÖ Connected - Room: {connectionId}</span>
          ) : connectionId ? (
            <span className="connecting">
              {isConnecting ? 'üîÑ Connecting...' : '‚è≥ Waiting for peer'} - Room: {connectionId}
            </span>
          ) : (
            <span className="disconnected">‚ùå Not Connected</span>
          )}
          {connectionId && (
            <button onClick={handleDisconnect} className="disconnect-btn">
              Disconnect
            </button>
          )}
          {connectionId && !isConnected && (
            <div className="debug-controls">
              <button 
                onClick={() => {
                  console.log('=== DEBUG INFO ===');
                  console.log('Room ID:', connectionId);
                  console.log('Is Host:', isHost);
                  console.log('Is Connecting:', isConnecting);
                  console.log('LocalStorage offer:', localStorage.getItem(`offer_${connectionId}`));
                  console.log('LocalStorage answer:', localStorage.getItem(`answer_${connectionId}`));
                  console.log('LocalStorage host ICE:', localStorage.getItem(`ice_${connectionId}_host`));
                  console.log('LocalStorage guest ICE:', localStorage.getItem(`ice_${connectionId}_guest`));
                  console.log('All localStorage keys:', Object.keys(localStorage).filter(k => k.includes(connectionId)));
                  if (webrtcManagerRef.current?.localConnection) {
                    console.log('Connection State:', webrtcManagerRef.current.localConnection.connectionState);
                    console.log('ICE Connection State:', webrtcManagerRef.current.localConnection.iceConnectionState);
                    console.log('Signaling State:', webrtcManagerRef.current.localConnection.signalingState);
                    console.log('Data Channel State:', webrtcManagerRef.current.dataChannel?.readyState);
                  }
                  console.log('=== END DEBUG ===');
                }}
                className="debug-btn"
              >
                üîç Debug Info
              </button>
              <button 
                onClick={() => {
                  console.log('üßπ Clearing localStorage for room:', connectionId);
                  localStorage.removeItem(`offer_${connectionId}`);
                  localStorage.removeItem(`answer_${connectionId}`);
                  localStorage.removeItem(`ice_${connectionId}_host`);
                  localStorage.removeItem(`ice_${connectionId}_guest`);
                  console.log('‚úÖ localStorage cleared');
                }}
                className="debug-btn"
              >
                üßπ Clear Storage
              </button>
              <button 
                onClick={() => {
                  console.log('üîÑ Restarting connection...');
                  handleDisconnect();
                  setTimeout(() => {
                    handleConnection(connectionId, isHost);
                  }, 1000);
                }}
                className="debug-btn"
              >
                üîÑ Restart
              </button>
            </div>
          )}
        </div>
      </header>

      {showConnectionPanel ? (
        <ConnectionPanel onConnect={handleConnection} />
      ) : isConnected ? (
        <div className="whiteboard-container">
          <div className="controls">
            <Toolbar
              currentTool={currentTool}
              setCurrentTool={setCurrentTool}
              currentColor={currentColor}
              setCurrentColor={setCurrentColor}
              strokeWidth={strokeWidth}
              setStrokeWidth={setStrokeWidth}
              onClear={handleClearPage}
            />
            <PageManager
              currentPage={currentPage}
              totalPages={totalPages}
              onPageChange={handlePageChange}
              onAddPage={addNewPage}
            />
          </div>

          <Whiteboard
            tool={currentTool}
            color={currentColor}
            strokeWidth={strokeWidth}
            onDrawing={handleLocalDrawing}
            drawingData={drawingData[currentPage] || []}
          />
        </div>
      ) : (
        <div className="waiting-screen">
          <div className="waiting-card">
            <h2>{isHost ? 'Room Created!' : 'Joining Room...'}</h2>
            <div className="room-display">
              <label>Room ID:</label>
              <div className="room-id-display">{connectionId}</div>
              <button 
                onClick={() => copyToClipboard(connectionId)}
                className="copy-btn"
              >
                üìã Copy Room ID
              </button>
            </div>

            {/* Manual Signaling Method */}
            <div className="manual-signaling">
              <h3>üîß Manual Connection Method</h3>
              <p>If automatic connection fails, use this manual method:</p>

              {isHost ? (
                <div className="host-instructions">
                  <h4>üì§ Host Instructions:</h4>
                  <ol>
                    <li>Copy the <strong>Offer Data</strong> below</li>
                    <li>Send it to the other person</li>
                    <li>Paste their <strong>Answer Data</strong> in the field below</li>
                  </ol>

                  <div className="signaling-data">
                    <label>Your Offer Data (copy this):</label>
                    <textarea 
                      id="offerData"
                      readOnly
                      rows="3"
                      className="signaling-textarea"
                      placeholder="Generating offer..."
                    />
                    <button 
                      onClick={() => {
                        const textarea = document.getElementById('offerData');
                        copyToClipboard(textarea.value);
                      }}
                      className="copy-btn small"
                    >
                      üìã Copy Offer
                    </button>
                  </div>

                  <div className="signaling-data">
                    <label>Paste Answer Data here:</label>
                    <textarea 
                      rows="3"
                      className="signaling-textarea"
                      placeholder="Paste the answer data from the other person here..."
                      onChange={(e) => {
                        if (e.target.value.trim()) {
                          try {
                            const answerData = JSON.parse(e.target.value);
                            console.log('üîç Parsed answer data:', answerData);
                            console.log('üîç Answer field:', answerData.answer);
                            console.log('üîç Answer type:', answerData.answer?.type);

                            // More flexible validation - check if it has answer data
                            if (answerData.answer && (answerData.answer.type === 'answer' || answerData.answer.sdp)) {
                              localStorage.setItem(`answer_${connectionId}`, JSON.stringify(answerData));
                              window.webrtcAnswers = window.webrtcAnswers || {};
                              window.webrtcAnswers[connectionId] = answerData;
                              console.log('‚úÖ Manually pasted answer data');
                            } else {
                              console.error('‚ùå Answer data structure:', JSON.stringify(answerData, null, 2));
                              console.error('‚ùå Expected: { answer: { type: "answer", sdp: "..." }, ... }');
                            }
                          } catch (error) {
                            console.error('‚ùå Invalid answer data format:', error);
                          }
                        }
                      }}
                    />
                  </div>

                  <div className="signaling-data">
                    <label>Your ICE Candidates (copy this):</label>
                    <textarea 
                      id="hostIceCandidates"
                      readOnly
                      rows="2"
                      className="signaling-textarea"
                      placeholder="ICE candidates will appear here..."
                    />
                    <button 
                      onClick={() => {
                        const textarea = document.getElementById('hostIceCandidates');
                        copyToClipboard(textarea.value);
                      }}
                      className="copy-btn small"
                    >
                      üìã Copy ICE
                    </button>
                  </div>

                  <div className="signaling-data">
                    <label>Paste Guest ICE Candidates here:</label>
                    <textarea 
                      rows="2"
                      className="signaling-textarea"
                      placeholder="Paste the ICE candidates from the guest here..."
                      onChange={(e) => {
                        if (e.target.value.trim()) {
                          try {
                            const iceData = JSON.parse(e.target.value);
                            localStorage.setItem(`ice_${connectionId}_guest`, JSON.stringify(iceData));
                            console.log('‚úÖ Manually pasted guest ICE candidates');
                            // Trigger ICE processing
                            if (webrtcManagerRef.current) {
                              webrtcManagerRef.current.processStoredIceCandidates('guest');
                            }
                          } catch (error) {
                            console.error('‚ùå Invalid ICE data format');
                          }
                        }
                      }}
                    />
                  </div>
                </div>
              ) : (
                <div className="guest-instructions">
                  <h4>üì• Guest Instructions:</h4>
                  <ol>
                    <li>Paste the <strong>Offer Data</strong> from the host below</li>
                    <li>Copy the generated <strong>Answer Data</strong></li>
                    <li>Send the Answer Data back to the host</li>
                    <li>Exchange <strong>ICE Candidates</strong> to complete connection</li>
                  </ol>

                  <div className="signaling-data">
                    <label>Paste Offer Data here:</label>
                    <textarea 
                      rows="3"
                      className="signaling-textarea"
                      placeholder="Paste the offer data from the host here..."
                      onChange={(e) => {
                        if (e.target.value.trim()) {
                          try {
                            const offerData = JSON.parse(e.target.value);
                            localStorage.setItem(`offer_${connectionId}`, JSON.stringify(offerData));
                            window.webrtcOffers = window.webrtcOffers || {};
                            window.webrtcOffers[connectionId] = offerData;
                            console.log('‚úÖ Manually pasted offer data');
                          } catch (error) {
                            console.error('‚ùå Invalid offer data format');
                          }
                        }
                      }}
                    />
                  </div>

                  <div className="signaling-data">
                    <label>Your Answer Data (copy this after pasting offer):</label>
                    <textarea 
                      id="answerData"
                      readOnly
                      rows="3"
                      className="signaling-textarea"
                      placeholder="Answer will appear here after you paste the offer data..."
                    />
                    <button 
                      onClick={() => {
                        const textarea = document.getElementById('answerData');
                        textarea.select();
                        // Try modern clipboard API first, fallback to execCommand
                        if (navigator.clipboard && navigator.clipboard.writeText) {
                          navigator.clipboard.writeText(textarea.value);
                        } else {
                          // Fallback for mobile/HTTP
                          document.execCommand('copy');
                        }
                      }}
                      className="copy-btn small"
                    >
                      üìã Copy Answer
                    </button>
                  </div>

                  <div className="signaling-data">
                    <label>Your ICE Candidates (copy this):</label>
                    <textarea 
                      id="guestIceCandidates"
                      readOnly
                      rows="2"
                      className="signaling-textarea"
                      placeholder="ICE candidates will appear here..."
                    />
                    <button 
                      onClick={() => {
                        const textarea = document.getElementById('guestIceCandidates');
                        copyToClipboard(textarea.value);
                      }}
                      className="copy-btn small"
                    >
                      üìã Copy ICE
                    </button>
                  </div>

                  <div className="signaling-data">
                    <label>Paste Host ICE Candidates here:</label>
                    <textarea 
                      rows="2"
                      className="signaling-textarea"
                      placeholder="Paste the ICE candidates from the host here..."
                      onChange={(e) => {
                        if (e.target.value.trim()) {
                          try {
                            const iceData = JSON.parse(e.target.value);
                            localStorage.setItem(`ice_${connectionId}_host`, JSON.stringify(iceData));
                            console.log('‚úÖ Manually pasted host ICE candidates');
                            // Trigger ICE processing
                            if (webrtcManagerRef.current) {
                              webrtcManagerRef.current.processStoredIceCandidates('host');
                            }
                          } catch (error) {
                            console.error('‚ùå Invalid ICE data format');
                          }
                        }
                      }}
                    />
                  </div>
                </div>
              )}
            </div>

            {isHost ? (
              <div className="instructions">
                <p>Share this Room ID with the other person:</p>
                <ol>
                  <li>Send them the Room ID: <strong>{connectionId}</strong></li>
                  <li>Have them open this app and click "Join Room"</li>
                  <li>They should enter the Room ID and click "Join Room"</li>
                  <li>You'll be connected automatically!</li>
                </ol>
              </div>
            ) : (
              <div className="instructions">
                <p>Connecting to room: <strong>{connectionId}</strong></p>
                <p>Waiting for the host to be online...</p>
              </div>
            )}
            <button onClick={handleDisconnect} className="back-btn">
              ‚Üê Back to Connection
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
