import React from 'react';
import './Toolbar.css';

const Toolbar = ({ 
  currentTool, 
  setCurrentTool, 
  currentColor, 
  setCurrentColor, 
  strokeWidth, 
  setStrokeWidth,
  onClear 
}) => {
  const tools = [
    { id: 'pen', name: 'Pen', icon: '‚úèÔ∏è' }
  ];

  const colors = [
    '#000000', '#FF0000', '#00FF00', '#0000FF',
    '#FFFF00', '#FF00FF', '#00FFFF', '#FFA500',
    '#800080', '#FFC0CB', '#A52A2A', '#808080'
  ];

  const strokeWidths = [1, 2, 4, 6, 8, 12];

  return (
    <div className="toolbar">
      <div className="toolbar-section">
        <h3>Tools</h3>
        <div className="tool-buttons">
          {tools.map(tool => (
            <button
              key={tool.id}
              className={`tool-btn ${currentTool === tool.id ? 'active' : ''}`}
              onClick={() => setCurrentTool(tool.id)}
              title={tool.name}
            >
              <span className="tool-icon">{tool.icon}</span>
              <span className="tool-name">{tool.name}</span>
            </button>
          ))}
        </div>
      </div>

      <div className="toolbar-section">
        <h3>Colors</h3>
        <div className="color-palette">
          {colors.map(color => (
            <button
              key={color}
              className={`color-btn ${currentColor === color ? 'active' : ''}`}
              style={{ backgroundColor: color }}
              onClick={() => setCurrentColor(color)}
              title={color}
            />
          ))}
        </div>
        <div className="custom-color">
          <input
            type="color"
            value={currentColor}
            onChange={(e) => setCurrentColor(e.target.value)}
            className="color-picker"
            title="Custom Color"
          />
        </div>
      </div>

      <div className="toolbar-section">
        <h3>Stroke Width</h3>
        <div className="stroke-width-controls">
          <div className="stroke-width-buttons">
            {strokeWidths.map(width => (
              <button
                key={width}
                className={`stroke-btn ${strokeWidth === width ? 'active' : ''}`}
                onClick={() => setStrokeWidth(width)}
              >
                <div 
                  className="stroke-preview"
                  style={{
                    width: `${Math.max(width, 2)}px`,
                    height: `${Math.max(width, 2)}px`,
                    backgroundColor: currentColor
                  }}
                />
                <span>{width}px</span>
              </button>
            ))}
          </div>
          <div className="stroke-slider">
            <input
              type="range"
              min="1"
              max="20"
              value={strokeWidth}
              onChange={(e) => setStrokeWidth(parseInt(e.target.value))}
              className="slider"
            />
            <span>{strokeWidth}px</span>
          </div>
        </div>
      </div>

      <div className="toolbar-section">
        <h3>Actions</h3>
        <button className="action-btn clear-btn" onClick={onClear}>
          üóëÔ∏è Clear Page
        </button>
      </div>
    </div>
  );
};

export default Toolbar;
