class WebRTCManager {
  constructor() {
    this.localConnection = null;
    this.dataChannel = null;
    this.isHost = false;
    this.roomId = null;
    this.onDataReceived = null;
    this.onConnectionStateChange = null;
    this.isConnected = false;
    this.answerProcessed = false;
    this.offerProcessed = false;
    this.answerPolling = null;
    this.offerPolling = null;

    // Use Google's STUN servers for NAT traversal
    this.iceServers = [
      { urls: 'stun:stun.l.google.com:19302' },
      { urls: 'stun:stun1.l.google.com:19302' },
      { urls: 'stun:stun2.l.google.com:19302' }
    ];
  }

  async initialize(roomId, isHost = false) {
    this.roomId = roomId;
    this.isHost = isHost;

    console.log(`üöÄ Initializing WebRTC - Room: ${roomId}, Role: ${isHost ? 'HOST' : 'GUEST'}`);
    console.log('üåê Current origin:', window.location.origin);

    // Clear any existing data for this room first
    this.cleanup();

    try {
      // Create RTCPeerConnection with optimized configuration
      this.localConnection = new RTCPeerConnection({
        iceServers: this.iceServers,
        iceCandidatePoolSize: 10,
        bundlePolicy: 'max-bundle',
        rtcpMuxPolicy: 'require'
      });

      // Set up comprehensive connection state monitoring
      this.localConnection.onconnectionstatechange = () => {
        console.log('üîÑ Connection state changed:', this.localConnection.connectionState);

        switch(this.localConnection.connectionState) {
          case 'connected':
            console.log('üéâ PEER CONNECTION ESTABLISHED!');
            break;
          case 'disconnected':
            console.log('‚ö†Ô∏è Peer connection disconnected');
            break;
          case 'failed':
            console.log('‚ùå Peer connection failed');
            break;
          case 'closed':
            console.log('üîí Peer connection closed');
            break;
        }
      };

      this.localConnection.oniceconnectionstatechange = () => {
        console.log('üßä ICE connection state changed:', this.localConnection.iceConnectionState);

        switch(this.localConnection.iceConnectionState) {
          case 'checking':
            console.log('üîç ICE checking connectivity...');
            break;
          case 'connected':
            console.log('‚úÖ ICE connected successfully!');
            break;
          case 'completed':
            console.log('üéØ ICE connection completed!');
            break;
          case 'failed':
            console.log('‚ùå ICE connection failed');
            break;
          case 'disconnected':
            console.log('‚ö†Ô∏è ICE disconnected');
            break;
          case 'closed':
            console.log('üîí ICE connection closed');
            break;
        }
      };

      this.localConnection.onsignalingstatechange = () => {
        console.log('üì° Signaling state changed:', this.localConnection.signalingState);
      };

      if (isHost) {
        await this.setupAsHost();
      } else {
        await this.setupAsGuest();
      }
    } catch (error) {
      console.error('‚ùå WebRTC initialization failed:', error);
      throw error;
    }
  }

  async setupAsHost() {
    console.log('üîµ Setting up as HOST for room:', this.roomId);

    // Create data channel with optimized settings
    this.dataChannel = this.localConnection.createDataChannel('whiteboard', {
      ordered: true,  // Ensure drawing data arrives in order
      maxRetransmits: 3
    });

    this.setupDataChannel(this.dataChannel);

    // Handle ICE candidates
    this.localConnection.onicecandidate = (event) => {
      if (event.candidate) {
        console.log('üßä Host ICE candidate generated');
        this.storeIceCandidate(event.candidate, 'host');
      } else {
        console.log('‚úÖ Host ICE gathering complete');
      }
    };

    // Create offer
    console.log('üìù Creating offer...');
    const offer = await this.localConnection.createOffer();
    await this.localConnection.setLocalDescription(offer);

    // Store offer in localStorage with multiple methods
    const offerData = {
      offer: offer,
      timestamp: Date.now(),
      roomId: this.roomId
    };

    // Method 1: Standard localStorage
    const offerKey = `offer_${this.roomId}`;
    localStorage.setItem(offerKey, JSON.stringify(offerData));

    // Method 2: Force localStorage sync with storage event
    window.dispatchEvent(new StorageEvent('storage', {
      key: offerKey,
      newValue: JSON.stringify(offerData),
      url: window.location.href
    }));

    // Method 3: Also store in global variable for same-page access
    window.webrtcOffers = window.webrtcOffers || {};
    window.webrtcOffers[this.roomId] = offerData;

    console.log('üíæ Offer stored with multiple methods:', offerData);
    console.log('üîç Verifying storage immediately:');
    console.log('  - localStorage:', localStorage.getItem(offerKey));
    console.log('  - window.webrtcOffers:', window.webrtcOffers[this.roomId]);

    // Populate manual signaling field
    setTimeout(() => {
      const offerTextarea = document.getElementById('offerData');
      if (offerTextarea) {
        offerTextarea.value = JSON.stringify(offerData);
        console.log('üìù Populated manual offer textarea');
      }
    }, 500);

    console.log('‚úÖ Host setup complete. Room ID:', this.roomId);
    console.log('‚è≥ Waiting for answer...');

    // Listen for answer
    this.waitForAnswer();
  }

  async setupAsGuest() {
    console.log('üü¢ Setting up as GUEST for room:', this.roomId);

    // Handle ICE candidates
    this.localConnection.onicecandidate = (event) => {
      if (event.candidate) {
        console.log('üßä Guest ICE candidate generated');
        this.storeIceCandidate(event.candidate, 'guest');
      } else {
        console.log('‚úÖ Guest ICE gathering complete');
      }
    };

    // Wait for offer
    console.log('üîç Looking for offer...');
    const offerData = await this.waitForOffer();
    if (!offerData) {
      throw new Error('No offer found for this room');
    }

    console.log('üì• Offer received, setting as remote description...');
    console.log('üìã Current signaling state:', this.localConnection.signalingState);

    try {
      await this.localConnection.setRemoteDescription(offerData.offer);
      console.log('‚úÖ Remote description (offer) set successfully');
    } catch (error) {
      console.error('‚ùå Failed to set remote description (offer):', error);
      throw error;
    }

    // Listen for data channel from host
    this.localConnection.ondatachannel = (event) => {
      console.log('üì° Data channel received from host');
      this.dataChannel = event.channel;
      this.setupDataChannel(this.dataChannel);
    };

    // Create answer
    console.log('üìù Creating answer...');
    const answer = await this.localConnection.createAnswer();
    console.log('üí° Answer created:', answer);

    await this.localConnection.setLocalDescription(answer);
    console.log('‚úÖ Local description (answer) set successfully');

    // Store answer using multiple methods
    const answerData = { type: 'answer', sdp: answer.sdp };
    localStorage.setItem(`answer_${this.roomId}`, JSON.stringify(answerData));

    // Global variable fallback
    window.webrtcAnswers = window.webrtcAnswers || {};
    window.webrtcAnswers[this.roomId] = answerData;

    console.log('üíæ Answer stored with multiple methods');

    // Auto-populate the answer textarea for manual copy/paste
    setTimeout(() => {
      const answerTextarea = document.getElementById('answerData');
      if (answerTextarea) {
        answerTextarea.value = JSON.stringify(answerData);
        console.log('üìù Auto-populated answer textarea');
      }
    }, 100);

    console.log('‚úÖ Guest setup complete');

    // Process stored ICE candidates from host
    setTimeout(() => {
      this.processStoredIceCandidates('host');
    }, 1000);
  }

  setupDataChannel(channel) {
    console.log('üîß Setting up data channel...');

    channel.onopen = () => {
      console.log('üéâ DATA CHANNEL OPENED - CONNECTION ESTABLISHED!');
      this.isConnected = true;
      if (this.onConnectionStateChange) {
        this.onConnectionStateChange(true);
      }
    };

    channel.onclose = () => {
      console.log('‚ùå Data channel closed');
      this.isConnected = false;
      if (this.onConnectionStateChange) {
        this.onConnectionStateChange(false);
      }
    };

    channel.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        console.log('üì® Data received:', data.type);
        if (this.onDataReceived) {
          this.onDataReceived(data);
        }
      } catch (error) {
        console.error('‚ùå Failed to parse received data:', error);
      }
    };

    channel.onerror = (error) => {
      console.error('‚ùå Data channel error:', error);
    };
  }

  async waitForOffer() {
    return new Promise((resolve, reject) => {
      let attempts = 0;
      const maxAttempts = 30;

      this.offerPolling = setInterval(() => {
        attempts++;
        console.log(`üîç Looking for offer... attempt ${attempts}/${maxAttempts}`);

        // Debug: Show all localStorage keys and global variables
        const allKeys = Object.keys(localStorage);
        const roomKeys = allKeys.filter(k => k.includes(this.roomId));
        console.log('üì¶ All localStorage keys:', allKeys.length);
        console.log('üì¶ Room-specific keys:', roomKeys);
        console.log('üåê Global offers:', window.webrtcOffers);

        // Try multiple methods to find the offer
        let offerData = null;

        // Method 1: localStorage
        const localStorageOffer = localStorage.getItem(`offer_${this.roomId}`);
        if (localStorageOffer) {
          console.log('‚úÖ Found offer in localStorage');
          offerData = localStorageOffer;
        }

        // Method 2: Global variable
        if (!offerData && window.webrtcOffers && window.webrtcOffers[this.roomId]) {
          console.log('‚úÖ Found offer in global variable');
          offerData = JSON.stringify(window.webrtcOffers[this.roomId]);
        }

        if (offerData) {
          try {
            const parsed = JSON.parse(offerData);
            console.log('üìÑ Found offer data:', parsed);

            // Check if offer is not too old (10 minutes)
            if (Date.now() - parsed.timestamp < 600000) {
              console.log('‚úÖ Valid offer found!');
              clearInterval(this.offerPolling);
              this.offerProcessed = true;
              resolve(parsed);
              return;
            } else {
              console.log('‚ùå Offer too old, removing...');
              localStorage.removeItem(`offer_${this.roomId}`);
              if (window.webrtcOffers) {
                delete window.webrtcOffers[this.roomId];
              }
            }
          } catch (error) {
            console.error('‚ùå Failed to parse offer:', error);
          }
        } else {
          console.log('‚ùå No offer found with any method');
        }

        if (attempts >= maxAttempts) {
          clearInterval(this.offerPolling);
          reject(new Error('Timeout waiting for offer'));
        }
      }, 1000);
    });
  }

  async waitForAnswer() {
    let attempts = 0;
    const maxAttempts = 30;

    this.answerPolling = setInterval(async () => {
      attempts++;
      console.log(`üîç Looking for answer... attempt ${attempts}/${maxAttempts}`);

      // Debug: Show localStorage and global variables
      const allKeys = Object.keys(localStorage);
      const roomKeys = allKeys.filter(k => k.includes(this.roomId));
      console.log('üì¶ Room-specific keys:', roomKeys);
      console.log('üåê Global answers:', window.webrtcAnswers);

      // Try multiple methods to find the answer
      let answerData = null;

      // Method 1: localStorage
      const localStorageAnswer = localStorage.getItem(`answer_${this.roomId}`);
      if (localStorageAnswer) {
        console.log('‚úÖ Found answer in localStorage');
        answerData = localStorageAnswer;
      }

      // Method 2: Global variable
      if (!answerData && window.webrtcAnswers && window.webrtcAnswers[this.roomId]) {
        console.log('‚úÖ Found answer in global variable');
        answerData = JSON.stringify(window.webrtcAnswers[this.roomId]);
      }

      if (answerData) {
        try {
          const parsed = JSON.parse(answerData);
          console.log('üìÑ Found answer data:', parsed);

          // Check connection state before setting remote description
          console.log('üìã Current signaling state:', this.localConnection.signalingState);

          if (this.localConnection.signalingState === 'have-local-offer') {
            console.log('‚úÖ Answer received, setting as remote description...');
            await this.localConnection.setRemoteDescription(parsed.answer);

            clearInterval(this.answerPolling);
            this.answerProcessed = true;

            // Process guest ICE candidates
            setTimeout(() => {
              this.processStoredIceCandidates('guest');
            }, 1000);

            console.log('‚úÖ Remote description (answer) set successfully');
            return;
          } else {
            console.log('‚ö†Ô∏è Wrong signaling state for answer:', this.localConnection.signalingState);
          }
        } catch (error) {
          console.error('‚ùå Failed to set remote description (answer):', error);
          clearInterval(this.answerPolling);
        }
      } else {
        console.log('‚ùå No answer found with any method');
      }

      if (attempts >= maxAttempts) {
        clearInterval(this.answerPolling);
        console.error('‚ùå Timeout waiting for answer');
      }
    }, 1000);
  }

  storeIceCandidate(candidate, role) {
    const key = `ice_${this.roomId}_${role}`;
    const existing = JSON.parse(localStorage.getItem(key) || '[]');
    existing.push({
      candidate: candidate,
      timestamp: Date.now()
    });
    localStorage.setItem(key, JSON.stringify(existing));
    console.log(`üíæ Stored ICE candidate for ${role}`);

    // Update manual ICE fields
    this.updateManualIceFields(role);
  }

  updateManualIceFields(role) {
    setTimeout(() => {
      const key = `ice_${this.roomId}_${role}`;
      const candidates = JSON.parse(localStorage.getItem(key) || '[]');

      if (role === 'host') {
        const hostTextarea = document.getElementById('hostIceCandidates');
        if (hostTextarea && candidates.length > 0) {
          hostTextarea.value = JSON.stringify(candidates);
          console.log(`üìù Updated host ICE textarea with ${candidates.length} candidates`);
        }
      } else if (role === 'guest') {
        const guestTextarea = document.getElementById('guestIceCandidates');
        if (guestTextarea && candidates.length > 0) {
          guestTextarea.value = JSON.stringify(candidates);
          console.log(`üìù Updated guest ICE textarea with ${candidates.length} candidates`);
        }
      }
    }, 100);
  }

  async processStoredIceCandidates(role) {
    const key = `ice_${this.roomId}_${role}`;
    const candidates = JSON.parse(localStorage.getItem(key) || '[]');

    console.log(`üßä Processing ${candidates.length} ICE candidates for ${role}`);

    for (const candidateData of candidates) {
      try {
        if (this.localConnection.signalingState !== 'closed') {
          await this.localConnection.addIceCandidate(candidateData.candidate);
          console.log(`‚úÖ Added ICE candidate for ${role}`);
        }
      } catch (error) {
        console.error(`‚ùå Failed to add ICE candidate for ${role}:`, error);
      }
    }

    // Check connection state after processing ICE candidates
    setTimeout(() => {
      console.log('üîç Current connection states:');
      console.log('  - Connection state:', this.localConnection.connectionState);
      console.log('  - ICE connection state:', this.localConnection.iceConnectionState);
      console.log('  - Data channel state:', this.dataChannel?.readyState);
    }, 2000);
  }

  sendData(data) {
    if (this.dataChannel && this.dataChannel.readyState === 'open') {
      try {
        this.dataChannel.send(JSON.stringify(data));
      } catch (error) {
        console.error('‚ùå Failed to send data:', error);
      }
    } else {
      console.warn('‚ö†Ô∏è Data channel not ready, state:', this.dataChannel?.readyState || 'null');
    }
  }

  cleanup() {
    if (this.roomId) {
      const keys = [`offer_${this.roomId}`, `answer_${this.roomId}`, `ice_${this.roomId}_host`, `ice_${this.roomId}_guest`];
      keys.forEach(key => localStorage.removeItem(key));

      // Clean global variables too
      if (window.webrtcOffers) {
        delete window.webrtcOffers[this.roomId];
      }
      if (window.webrtcAnswers) {
        delete window.webrtcAnswers[this.roomId];
      }

      console.log('üßπ Cleaned up localStorage and global variables for room:', this.roomId);
    }
  }

  disconnect() {
    console.log('üîå Disconnecting WebRTC...');

    // Clear polling intervals
    if (this.answerPolling) {
      clearInterval(this.answerPolling);
      this.answerPolling = null;
    }
    if (this.offerPolling) {
      clearInterval(this.offerPolling);
      this.offerPolling = null;
    }

    this.isConnected = false;
    this.answerProcessed = false;
    this.offerProcessed = false;

    if (this.dataChannel) {
      this.dataChannel.close();
    }
    if (this.localConnection) {
      this.localConnection.close();
    }

    this.cleanup();
    console.log('‚úÖ Disconnection complete');
  }
}

export default WebRTCManager;
