import React from 'react';

const Toolbar = ({ 
  currentTool, 
  setCurrentTool, 
  currentColor, 
  setCurrentColor, 
  strokeWidth, 
  setStrokeWidth, 
  onClear 
}) => {
  const colors = [
    '#000000', '#FF0000', '#00FF00', '#0000FF',
    '#FFFF00', '#FF00FF', '#00FFFF', '#FFA500',
    '#800080', '#008000', '#808080', '#FFFFFF'
  ];

  const strokeSizes = [1, 2, 3, 5, 8, 12];

  return (
    <>
      {/* Pen Tool */}
      <button
        className={`tool-btn ${currentTool === 'pen' ? 'active' : ''}`}
        onClick={() => setCurrentTool('pen')}
        title="Pen Tool"
      >
        ‚úèÔ∏è
      </button>

      {/* Divider */}
      <div className="divider"></div>

      {/* Color Palette */}
      <div className="color-grid">
        {colors.map(color => (
          <button
            key={color}
            className={`color-btn ${currentColor === color ? 'active-color' : ''}`}
            style={{ backgroundColor: color }}
            onClick={() => setCurrentColor(color)}
            title={`Color: ${color}`}
          />
        ))}
      </div>

      {/* Divider */}
      <div className="divider"></div>

      {/* Stroke Width */}
      <div className="stroke-grid">
        {strokeSizes.map(size => (
          <button
            key={size}
            className={`stroke-btn ${strokeWidth === size ? 'active-stroke' : ''}`}
            onClick={() => setStrokeWidth(size)}
            title={`Stroke: ${size}px`}
          >
            <div 
              className="stroke-preview"
              style={{ 
                width: `${Math.min(size * 2, 16)}px`, 
                height: `${Math.min(size * 2, 16)}px`,
                backgroundColor: currentColor
              }}
            />
          </button>
        ))}
      </div>

      {/* Divider */}
      <div className="divider"></div>

      {/* Clear Button */}
      <button
        className="clear-btn"
        onClick={onClear}
        title="Clear Page"
      >
        üóëÔ∏è
      </button>
    </>
  );
};

export default Toolbar;
